import React, { useState, useEffect } from "react";
import AccountContainer from "./AccountContainer";


function App() {
  const [transactions, setTransactions] = useState([])
  const [searchTerm, setSearchTerm] = useState('')

  // const handleAddTransaction = (transaction) => {
  //   setTransactions = [...transaction, transaction];
  // }

  useEffect(() => {
    fetch('http://localhost:8001/transactions').then(resp => resp.json())
      .then(data => {
        setTransactions(data)
        console.log(data)
      })
  }, [])

  const addTransaction = (date, description, category, amount) => {
    fetch('http://localhost:8001/transactions',
      {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "accept": 'application/json'
        }, body: JSON.stringify({
          date,
          description,
          category,
          amount,
        })
      }).then(resp => resp.json()).then(data => console.log(data))

  }



  const handleSearch = transactions.filter((transaction) => {
    if (searchTerm === "") {
      return true;
    } else if (transaction.description.toLowerCase().include(searchTerm.toLowerCase())) {
      return transaction.description
    }
  })

  function handleFilter(event) {
    setSearchTerm(event.target.value);
  }



  return (
    <div className="ui raised segment">
      <div className="ui segment violet inverted">
        <h2>The Royal Bank of Flatiron</h2>
      </div>
      <AccountContainer transactions={transactions} addTransaction={addTransaction} />
    </div>
  );
}

export default App;
